import React from "react"

class TodoItem extends React.Component {
    constructor() {
        super()
        this.state = {
            clicks: 0
        }
        this.handleClick = this.handleClick.bind(this)
    }

    handleClick() {
        this.setState(prevState => {
            return {
                clicks: this.state.clicks + 1
            }
        })
    }

    render(props) {
        return (
            <div>
                <input type="checkbox" checked={this.props.obj.completed} onClick={this.handleClick}></input>
                {this.props.obj.name} {this.state.clicks}
            </div>
        )
    }
}

export default TodoItem
