import React from "react"
import Header from "./components/Header"
import MainContent from "./components/MainContent"
import Footer from "./components/Footer"

var my = 0

function hello(){
    console.log("I was clicked " + my)
    my = my + 1
}

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            who: "folks"
        }
    }
    render() {
        return(
            <div>
                <Header />
                <MainContent />
                <p onMouseOver={hello}>That's all {this.state.who}</p>
                <Footer />
            </div>
        )
    }
}

export default App
