import React from "react"

class TodoItem extends React.Component {
    constructor() {
        super()
        this.state = {
            count: 0
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange() {
        this.setState(prevState => {
            return {
                count: prevState.count + 1
            }
        })
    }

    render(props) {
        return(
            <div>
                <input type="checkbox" checked={this.props.obj.completed} onChange={() => this.props.handleChange(this.props.obj.id)}/>
                {this.props.obj.name} {this.state.count}
            </div>
        )
    }
}

export default TodoItem
